* {
	margin: 0;
	padding: 0;
	border: 0;
	font-size: 100%;
	font-weight: normal;
	vertical-align: baseline;
	-webkit-box-sizing: border-box;
	-moz-box-sizing: border-box;
	box-sizing: border-box;
}

*:before, *:after {
	-webkit-box-sizing: border-box;
	-moz-box-sizing: border-box;
	box-sizing: border-box;
}

html, body {
	min-height: 100%;
}

body {
	background-color: #212529;
}

.container {
	background-color: #212529;
	color: #000000;
	max-width: 100% !important;
	margin-left: 0;
	padding-left: 0;
}

.row {
	margin-right: 0 !important;
    margin-left: 0 !important;
}

.control-cont {
	padding-left: 5px !important;
}

.btn-bbb {
    color: #fbfbfb;
    background-color: #12646f !important;
    border-color: #12646f !important;
}

.btn-bbb:hover {
    color: #fbfbfb;
    background-color: #53b0bd !important;
    border-color: #53b0bd !important;
}

.btn-bbb:active {
    color: #fbfbfb;
    background-color: #53b0bd !important;
    border-color: #53b0bd !important;
}

/* MAP */
#map {
	max-width:100%;
	max-height:100%;
	height: 95vh; /* 100% of viewport height*/
}

.clock {
	position: absolute;
	display: flex;
	align-items: center;
    top: 5%;
    left: 25%;
    transform: translateX(-50%) translateY(-50%);
    color: #fbfbfb;
    font-size: 30px;
	letter-spacing: 7px;
	padding-left: 7px;
    z-index: 100;
    background-color: #32383e;
}

.clockweek{
	top: 5%;
	left: 17%;
}

.ol-attribution button{
	display: none !important;
}
/* MAP END*/

/* TIMELINE SLIDER*/
.slider {
    -webkit-appearance: none;
    width: 100%;
	/* height: 25px; */
	height: 4vh;
    background: #d3d3d3;
    outline: none;
    opacity: 0.7;
    -webkit-transition: .2s;
    transition: opacity .2s;
}

.slider:hover {
    opacity: 1;
}

.slider::-webkit-slider-thumb {
    -webkit-appearance: none;
    appearance: none;
    width: 25px;
    height: 1vh;
    background: #ff0000;
    cursor: pointer;
}

.slider::-moz-range-thumb {
    width: 25px;
    height: 5px;
    background: #ff0000;
    cursor: pointer;
}

.summary2 {
	display: flex;
    align-items: center;
    justify-content: center;
	position: absolute;
    top: 600px;
	z-index: 1;
}
/* TIMELINE SLIDER END*/


