/* LOGO */
.img-log {
	max-width:100%;
	max-height:100%;
	height: 100px;
	display: flex;
	align-items: center;
	justify-content: center; 
}


.img-log img {
	margin: 0;
	width: 20% !important;
	/* animation: App-logo-spin infinite 20s linear; */
}

@keyframes App-logo-spin {
	from { transform: rotate(0deg); }
	to { transform: rotate(360deg); }
  }
/* LOGO END */


/* SUGGESTION */

.list_suggestions {
	list-style-type: none;
	margin: 0;
	padding: 0;
  }

.list_suggestions  li {
	font-size: 12px;
	font-weight: 200px;
	border-bottom: 1px solid #c2b394;
	padding: 5px;
}

.list_suggestions li:last-child {
	border: none;
}

.list_suggestions li a {
	text-decoration: none;
	color: #fbfbfb;
	display: block;
	width: 200px;
	-webkit-transition: font-size 0.3s ease, background-color 0.3s ease;
	-moz-transition: font-size 0.3s ease, background-color 0.3s ease;
	-o-transition: font-size 0.3s ease, background-color 0.3s ease;
	-ms-transition: font-size 0.3s ease, background-color 0.3s ease;
	transition: font-size 0.3s ease, background-color 0.3s ease;
}

.list_suggestions  li a:hover {
	font-size: 10px;
	background: #12646f;
	color: #fbfbfb;
}
/* SUGGESTION END*/


/* FORM */
.inner-addon { 
	position: relative; 
}

/* style icon */
.inner-addon .fa-map-marker-alt {
  position: absolute;
  padding: 10px;
  pointer-events: none;
  color: #000000;
}

/* align icon */
.left-addon .fa-map-marker-alt  { left:  0px;}
.right-addon .fa-map-marker-alt { right: 0px;}

/* add padding  */
.left-addon input  { padding-left:  30px; }
.right-addon input { padding-right: 30px; }
/* FORM END*/

